"use strict";

var getFileStats = require("./getFileStats")

/**
 * Returns one of directory|file|null
 */
function arFileStatus(path) {
    return getFileStats(path)
        .then(function (stats) {

            if (stats && stats.isFile()) {
                return 'file';
            }

            if (stats && stats.isDirectory()) {
                return 'directory'
            }

            return null
        });
}

module.exports = arFileStatus;